const initialState = {
    shows: [], loading:false
  }


const showReducer = (state = initialState, action) => {
    if (action.type === 'ADD_SHOW') {
        console.log(state)


        return {
            ...state,
            shows: [...state, action.payload]
        };





    } else if (action.type === 'GET_SHOWS') {

        return state.shows; //not needed perhaps since default returns state
    }
    return state;
}


export default showReducer;
