const initialState = {
    shows: [1, 2, 3]
  }


const showReducer = (state = [], action) => {
    if (action.type === 'ADD_SHOW') {

const { payload } = action
        return 
             [...state, payload]
        ;





    } else if (action.type === 'GET_SHOWS') {

        return state.shows; //not needed perhaps since default returns state
    }
    return state;
}


export default showReducer;
