export const showAddReducer = (state = {selected: null, shows:[]}, action) => {
    if (action.type === 'ADD_SHOW') {
        const { payload } = action;
        console.log(state);
        return [...state, payload];

    } 

    if (action.type === 'DELETE_SHOW'){
        for (let i = 0; i < state.length; i++){
            if (state[i].showId === action.payload){
                return [...state.filter(item => item.showId !== action.payload)]
            }
        }

    }

    if (action.type === 'SELECT_SHOW'){
        console.log("hello michael");
        console.log([...state]);
        console.log(state[action.payload]);

        return 
    }


    return state;
}



