import React from 'react';
import { connect } from 'react-redux';
import { addShow, getShows } from '../actions';

import Show from './show';

class ShowList extends React.Component {

    state = { song: ''};

    renderList() {
        return this.props.shows.map((show) => 
            <Show name={show}></Show>
        ); 
    }


    render() {
        let inputText;
        return (
            <div >
                <p className="listP">here is the show list</p>

{console.log(this.props.shows)}
                <form className="cssGridForm" onSubmit={(e) => {
                    console.log(e.target)
                    e.preventDefault();
                    this.props.addShow(inputText.value)}

                    }>
                    <input
                    className="band"
                    type="text"
                    name="show"
                    placeholder="Band"
                    ref={node => inputText = node} //learn this input ref stuff




                    />

                    <input
                    className="date"
                    type="text"
                    name="date"
                    placeholder="Date"
                    ref={node => inputText = node} //learn this input ref stuff
/>
                    <input
                    className="venue"
                    type="text"
                    name="venue"
                    placeholder="Venue"
                    ref={node => inputText = node} //learn this input ref stuff
                    />
                    <button type="submit"
                    >Add show</button>
                </form>


                {this.renderList()}

            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return { shows: state.listOfShows };
}

export default connect(mapStateToProps, {addShow, getShows})(ShowList);
