import React from 'react';
import { connect } from 'react-redux';
import { addShow, getShows } from '../actions';

import Show from './show';

class ShowList extends React.Component {

    // componentDidMount() {
    //     const allTheShows = this.props.getShows();
    //     console.log(allTheShows);
    // }

    renderList() {
        return this.props.shows.map((show) => 
            <Show name={show}></Show>
        ); 
    }


    render() {
        return (
            <div >
                <p className="listP">here is the show list</p>


                <form onSubmit={(e) => this.props.addShow(e.target.value)}>
                    <input
                    name="show"
                    />
                    <button type="submit"
                    >Submit</button>
                </form>


                {this.renderList()}

            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return { shows: state.listOfShows };
}

export default connect(mapStateToProps, {addShow, getShows})(ShowList);
