import React from 'react';
import { connect } from 'react-redux';
import { addShow, getShows } from '../actions';

import Show from './show';

class ShowList extends React.Component {

    // componentDidMount() {
    //     const allTheShows = this.props.getShows();
    //     console.log(allTheShows);
    // }


    render() {
       console.log('rerender');
        return (
            <div>
                here is the show list

                <button onClick={() => this.props.addShow('northlane')}>
                    here is text so the button is large
                </button>
{console.log(this.props.shows)}
                {(this.props.shows !== undefined) ? this.props.shows.map(() => 
                    <Show></Show>
                ) : <div>no shows yet</div> }

            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return { shows: state.showList };
}

export default connect(mapStateToProps, {addShow, getShows})(ShowList);

