import React from 'react';
import { connect } from 'react-redux';
import { addShow } from '../actions';

import Show from './show';

class ShowList extends React.Component {



    render() {
        return (
            <div>
                here is the show list

                <button onClick={() => {this.props.addShow('northlane')}}>
                    here is text so the button is large
                </button>

                {this.props.shows.map(() => {
                    <Show></Show>
                })}
            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return { shows: state.showList };
}

export default connect(mapStateToProps, {addShow})(ShowList);
