let showId = 0;

export const addShow = (showName, venue, date) => {
    let showObject= {
        showName,
        venue,
        date,
        showId
    };
    showId++;
    return {
        type: 'ADD_SHOW',
        payload: showObject
    };
}

export const getShows = () => {
    return {
        type: 'GET_SHOWS'
    };
}

export const deleteShow = (deleteThisOne) => {
    console.log("we kind of made it fam");
    return {
        type: 'DELETE_SHOW',
        payload: deleteThisOne
    };
}
